import useUser from "./useUser";
import api from '../Axios/axios'

const useLogin = ({email, password}) => {

    const { user, setUser } = useUser()

    async function call() {
        try {
            console.log(email)
            const data = (await api.post('/auth/login', {
                    email:email,
                    password:password
            })).data;
            if (data) {
                setUser(data);
            }
            return data;

        } catch (error) {

            return null
        }
    }

    return call;
}

export default useLogin
